mapper.select:
select * from t_user where 1=1

	and login_name = #{loginName}


	and phone = #{phone}

 and password = md5(#{password})

select count(id) from t_user where 1 = 1

		and phone = #{phone}


		and login_name = #{loginName}

mapper.update:
update t_user 
set password = md5(#{password})
where id = #{id}

